        cout << array[i] << " ";
    }
    cout << "\n\nPresione cualquier tecla para volver";
    getch();
}

void buscarDato(vector<int> array) {
    system("cls");
    int busc;
    cout << "Que elemento desea encontrar?: ";
    cin >> busc;
    if (find(array.begin(), array.end(), busc) != array.end()) {
        cout << "El elememto se encuentra en la posicion: " << find(array.begin(), array.end(), busc) - array.begin() << endl;
    } else {
        cout << "El elemento no se encuentra en el arreglo" << endl;
    }
    cout << "\nPresione cualquier tecla para volver";
    getch();
}

vector<int> elimDato(vector<int> array) {
    system("cls");
    int elim;
    cout << "Que elemento desea eliminar?: ";
    cin >> elim;
    array.erase(find(array.begin(), array.end(), elim));
    cout << "\nElemento eliminado... presione cualquier tecla para volver";
    getch();
    return array;
}

void mostrarMenu(vector<int> array, char op) {
