#include <iostream>
#include <string.h>

using namespace std;

// Variables
int Edad[50],i,j,N,AuxE;
char Nom[50][35],AuxN[35],DNI[50][9],AuxD[8]; //Limitando DNI a 9 y no a 8 para evitar errores de concatenación

// Bubble Sort
void organizar(int a, int b){
    AuxE    = Edad[a];
	Edad[a] = Edad[b];
	Edad[b] = AuxE;   
	/*****************/
	strcpy(AuxD,DNI[a]);	
	strcpy(DNI[a],DNI[b]); 
	strcpy(DNI[b],AuxD);	
	/*****************/
	strcpy(AuxN,Nom[a]);	//AuxN  = Nom[i];
	strcpy(Nom[a],Nom[b]);  //Nom[i] = Nom[j];
	strcpy(Nom[b],AuxN);
}

// Mostar Datos
void mostrar(){
	for(i=0;i<N;i++){
		cout<<"---------------\nAlumno "<<i+1<<"\nNombre: "<<Nom[i]<<endl;
		cout<<"DNI: "<<DNI[i]<<endl;
		cout<<"Edad: "<<Edad[i]<<endl;
	}
}

int main(int argc, char** argv) {
    system("cls");
	cout<<"Cuantos datos ingresara?: ";
	cin>>N;
	cin.ignore();
	for(i=0;i<N;i++){
		cout<<"\nAlumno "<<i+1<<"\nNombre: ";
		fflush(stdin);
		cin.getline(Nom[i],35,'\n');
		cout<<"DNI: ";
		cin>>DNI[i];
		cout<<"Edad: ";
		cin>>Edad[i];
		cin.ignore();
	}
	system("cls");
	
	cout<<"Mostrando Nombre, DNI y Edad: \n";
	mostrar();
    //*************************************************************
    //ORDENANDO POR EDAD EN FORMA ASCENDENTE
    for(i=0;i<N-1;i++)
        for(j=i+1;j<N;j++)
            if(Edad[i]>Edad[j]){
                organizar(i,j);
            }
	
	cout<<"\n\nMostrando orden ascendente por edad: \n";
	mostrar();	
	//*************************************************************
	//ORDENANDO POR NOMBRE EN FORMA ASCENDEnTE	
    for(i=0;i<N-1;i++)
        for(j=i+1;j<N;j++)
            if(strcmp(Nom[i], Nom[j])>0){
                organizar(i,j);
            }	
    cout<<"\n\nMostrando orden ascendente por Nombre: \n";
	mostrar();

	return 0;
}

